  CREATE TABLE competencias (
  Id_Competencia varchar(20) NOT NULL,
  Competencia varchar(200) DEFAULT NULL,
  Norma varchar(200) DEFAULT NULL,
  Tipo_Competencia varchar(45) DEFAULT NULL,
  Conocimiento_Proceso varchar(3000) DEFAULT NULL,
  Conocimiento_Saber varchar(3000) DEFAULT NULL,
  Horas_Competencia int DEFAULT NULL,
  PRIMARY KEY (Id_Competencia)
);
CREATE TABLE nivel (
  Id_NIvel varchar(20) NOT NULL,
  Nombre_Nivel varchar(45) DEFAULT NULL,
  PRIMARY KEY (Id_NIvel)
); 
CREATE TABLE resultados_aprendizaje (
  Id_Resultado varchar(20) NOT NULL,
  Nombre varchar(300) DEFAULT NULL,
  PRIMARY KEY (Id_Resultado)
);
CREATE TABLE programas_formacion (
  Codigo_Programa varchar(10) NOT NULL,
  Id_Programa_Nivel varchar(10) DEFAULT NULL,
  Nombre_Programa varchar(100) DEFAULT NULL,
  Horas_Etapa_Lectiva int DEFAULT NULL,
  Horas_Etapa_Productiva int DEFAULT NULL,
  PRIMARY KEY (Codigo_Programa),
  KEY `FK-Programa_Nivel_idx` (Id_Programa_Nivel),
  CONSTRAINT `FK-Programa_Nivel` FOREIGN KEY (Id_Programa_Nivel) REFERENCES nivel (Id_NIvel)
);
CREATE TABLE aux_comp_rap (
  Id_Aux_Comp_Rap int NOT NULL,
  Id_Aux_Comp_Rap_Competencia varchar(20) DEFAULT NULL,
  Id_Aux_Comp_Rap_Resultado varchar(20) DEFAULT NULL,
  PRIMARY KEY (Id_Aux_Comp_Rap),
  KEY `FK-Aux_Comp_Rap_Competencia_idx` (Id_Aux_Comp_Rap_Competencia),
  KEY `FK-Aux_Comp_Rap_Resultado_idx` (Id_Aux_Comp_Rap_Resultado),
  CONSTRAINT `FK-Aux_Comp_Rap_Competencia` FOREIGN KEY (Id_Aux_Comp_Rap_Competencia) REFERENCES competencias (Id_Competencia),
  CONSTRAINT `FK-Aux_Comp_Rap_Resultado` FOREIGN KEY (Id_Aux_Comp_Rap_Resultado) REFERENCES resultados_aprendizaje (Id_Resultado));
  
  CREATE TABLE aux_prog_comp (
  Id_Aux_Prog_Comp varchar(15) NOT NULL,
  Id_Aux_Prog_Comp_Competencia varchar(45) DEFAULT NULL,
  Id_Aux_Prog_Comp_Programa varchar(45) DEFAULT NULL,
  PRIMARY KEY (Id_Aux_Prog_Comp),
  KEY `FK-Conocimiento_Competencia_idx` (Id_Aux_Prog_Comp_Competencia),
  KEY `FK-Conocimiento_Programa_idx` (Id_Aux_Prog_Comp_Programa),
  CONSTRAINT `FK-Aux_Prog_Comp_Competencia` FOREIGN KEY (Id_Aux_Prog_Comp_Competencia) REFERENCES competencias (Id_Competencia),
  CONSTRAINT `FK-Aux_Prog_Comp_Programa` FOREIGN KEY (Id_Aux_Prog_Comp_Programa) REFERENCES programas_formacion (Codigo_Programa)
);
